def substitute(ciphertext, mapping):
    substituted_text = []
    for char in ciphertext:
        substituted_char = mapping.get(char, char)
        substituted_text.append(substituted_char)
    return ''.join(substituted_text)

# Call the substitution function and print the result
result = substitute(ciphertext, substitution_map)
